export default function yoMitt() {
  // ÊâÄÊúâ‰∫ã‰ª∂
  let all = new Map()

  return {
    /**
     * ËÆ¢ÈòÖÊñπÊ≥ï
     * @param {*} key  ‰º†ÈÄíÁöÑ‰∫ã‰ª∂key
     * @param {*} handler  ‰∫ã‰ª∂Â§ÑÁêÜÂáΩÊï∞
     */
    on(key: string, handler: Function) {
      const handlers = all.get(key) || []
      handlers.push(handler)
      // Ê∑ªÂä†ÂØπÂ∫îÁöÑ‰∫ã‰ª∂
      all.set(key, handlers)
    },
    /**
     * ÂèëÂ∏ÉÊñπÊ≥ï
     * @param {*} key ÂèëÂ∏ÉÁªôËÆ¢ÈòÖËÄÖÁöÑkey
     * @param {*} params  ÂèëÂ∏ÉËÄÖÁöÑÂèÇÊï∞ÔºåÂëäËØâËÆ¢ÈòÖËÄÖÊàëÂèëÂ∏É‰∫Ü‰ªÄ‰πàÂÜÖÂÆπ„ÄÇ
     */
    emit(key: string, params?: any) {
      const handlers = all.get(key)
      // ÊâßË°åËÆ¢ÈòÖËÄÖÁöÑ‰∫ã‰ª∂
      if (handlers) {
        handlers.map((handler: Function) => {
          handler(params)
        })
      }
    },
    // ...
  }
}
